mapper.insert:
	    INSERT INTO tbl_quotation_confirm (
	            QUOTATION_REQ_NO
	            , cust_id
	            , driver_id
	            , confrim_dt
	            , cid
	        )
        VALUES (
            #{quotationReqNo}
            , #{custId}
            , #{driverId}
            , #{confirmDt}
            , #{cid}
        )

mapper.update:
	UPDATE tbl_quotation_confirm
    SET 
        QUOTATION_REQ_NO = #{quotationReqNo}
        , cust_id = #{custId}
        , driver_id = #{driverId}
        , confrim_dt = #{confrimDt}
        , uid = #{uid}
        , udt = CURRENT_TIMESTAMP()
    WHERE QUOTATION_REQ_NO = #{quotationReqNo}
