mapper.insert:
INSERT INTO tbl_quotation_request (
     QUOTATION_REQ_NO
     , QUOTATION_DT
     , CUST_ID
     , PICKUP_ADDR
     , DELIVERY_ADDR
     , MOVE_DT
     , QUOTATION_AMOUNT
     , BUILDING_TYPE
     , ROOM_STRUCTURE
     , HOUSE_SIZE
     , HAS_ELEVATOR
     , BOX_COUNT
     , CID
 )
 VALUES (
     #{quotationReqNo}
     , #{quotationDt}
     , #{custId}
     , #{pickupAddr}
     , #{deliveryAddr}
     , #{moveDt}
     , #{quotationAmount}
     , #{buildingType}
     , #{roomStructure}
     , #{houseSize}
     , #{hasElevator}
     , #{boxCount}
     , #{cid}
 )

mapper.update:
UPDATE tbl_quotation_request
SET 
    QUOTATION_DT = #{quotationDt}
    , CUST_ID = #{custId}
    , PICKUP_ADDR = #{pickupAddr}
    , DELIVERY_ADDR = #{deliveryAddr}
    , MOVE_DT = #{moveDt}
    , QUOTATION_AMOUNT = #{quotationAmount}
    , BUILDING_TYPE = #{buildingType}
    , ROOM_STRUCTURE = #{roomStructure}
    , HOUSE_SIZE = #{houseSize}
    , HAS_ELEVATOR = #{hasElevator}
    , BOX_COUNT = #{boxCount}
    , UID = #{uid}
    , UDT = CURRENT_TIMESTAMP()
WHERE QUOTATION_REQ_NO = #{quotationReqNo}
